import 'core-js/es6';

import { ok } from '../../test/test-helpers';
import { createChart, destroyChart }  from '../test-helpers';

let chart;

describe("Chart / Box Plot /", () => {
    afterEach(() => {
        destroyChart(chart);
    });

    it("setting 0 for median doesn't blow up", () => {
        chart = createChart({
            series: [{
                type: "boxPlot",
                data: [{"LowerQ1": 1, "Q1": 1, "Median": 2, "Mean": 0, "Q3": 3, "UpperQ3": 4}],
                lowerField: "LowerQ1",
                q1Field: "Q1",
                medianField: "Median",
                q3Field: "Q3",
                upperField: "UpperQ3",
                meanField: "Mean"
            }]
        });

        ok(true);
    });
});